        throw new Error('The database is a JavaScript file but the export is not a function.');
      } // Run dataFn to generate data


      const data = dataFn();
      resolve(low(new Memory()).setState(data));
    } else {
      throw new Error(`Unsupported source ${source}`);
    }
  });
